package com.borani.functional.supplier;public class Controller {    Logger logger = new Logger(Boolean.FALSE);    /*    However, imagine if the generateLogMessage method involves heavy processing or consumes significant resources,    and logging is disabled. In such cases, valuable resources are wasted as the produced log message wonâ€™t be utilised.     */    public void execute() {        logger.log(generateLogMessage());    }    private String generateLogMessage() {        /* heavy processing of the logic like DB operation of inter process microservices communication */        return null;    }}class Logger {    boolean logEnabled = false;    public Logger(boolean lobEnabled) {        this.logEnabled = lobEnabled;    }    public void log(String message) {        if (isLogEnabled()) {            System.out.println("Hello " + message);        }    }    private boolean isLogEnabled() {        return logEnabled;    }}